//If it is not empty then check for email validation
if(/^\s*$/.test(this.view.txtEmailR.text))
//Reset the skin to Normal since the textbox is empty
   {
	  self.view.txtEmailR["skin"] = "skEmailRounded"
      return    
   }
   else
   {
	if (/^\w+([\.-]?\ w+)*@\w+([\.-]?\ w+)*(\.\w{2,3})+$/.test(this.view.txtEmailR.text))
		{
		  self.view.txtEmailR["skin"] = "skEmailRounded"
		}
	else
	  {
		  self.view.txtEmailR["skin"] = "skEmailRoundedRed"

	}     
   }
